#!/bin/bash

# 🚀 Script de Inicialização Completa - ImóveisCaixa Pro
# Configura todo o ambiente de desenvolvimento e produção

set -e  # Parar em caso de erro

# Cores para output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
NC='\033[0m' # No Color

# Função para log colorido
log() {
    echo -e "${GREEN}[$(date +'%Y-%m-%d %H:%M:%S')] $1${NC}"
}

warn() {
    echo -e "${YELLOW}[WARNING] $1${NC}"
}

error() {
    echo -e "${RED}[ERROR] $1${NC}"
    exit 1
}

# Verificar se está rodando como root
if [[ $EUID -eq 0 ]]; then
   error "Este script não deve ser executado como root"
fi

log "🚀 Iniciando configuração do ImóveisCaixa Pro..."

# 1. Verificar dependências do sistema
log "📋 Verificando dependências do sistema..."

# Verificar PHP 8.3
if ! command -v php &> /dev/null || [[ $(php -r "echo PHP_MAJOR_VERSION.'.'.PHP_MINOR_VERSION;") < "8.3" ]]; then
    warn "PHP 8.3+ não encontrado. Instalando..."
    sudo apt update
    sudo apt install -y software-properties-common
    sudo add-apt-repository ppa:ondrej/php -y
    sudo apt update
    sudo apt install -y php8.3 php8.3-cli php8.3-fpm php8.3-mysql php8.3-pgsql \
        php8.3-xml php8.3-curl php8.3-gd php8.3-mbstring php8.3-zip \
        php8.3-intl php8.3-bcmath php8.3-redis php8.3-imagick
fi

# Verificar Composer
if ! command -v composer &> /dev/null; then
    warn "Composer não encontrado. Instalando..."
    curl -sS https://getcomposer.org/installer | php
    sudo mv composer.phar /usr/local/bin/composer
    sudo chmod +x /usr/local/bin/composer
fi

# Verificar Node.js 20+
if ! command -v node &> /dev/null || [[ $(node -v | cut -d'v' -f2 | cut -d'.' -f1) -lt 20 ]]; then
    warn "Node.js 20+ não encontrado. Instalando..."
    curl -fsSL https://deb.nodesource.com/setup_20.x | sudo -E bash -
    sudo apt-get install -y nodejs
fi

# Verificar PostgreSQL
if ! command -v psql &> /dev/null; then
    warn "PostgreSQL não encontrado. Instalando..."
    sudo apt install -y postgresql postgresql-contrib
    sudo systemctl start postgresql
    sudo systemctl enable postgresql
fi

# Verificar Redis
if ! command -v redis-cli &> /dev/null; then
    warn "Redis não encontrado. Instalando..."
    sudo apt install -y redis-server
    sudo systemctl start redis
    sudo systemctl enable redis
fi

# Verificar Nginx
if ! command -v nginx &> /dev/null; then
    warn "Nginx não encontrado. Instalando..."
    sudo apt install -y nginx
    sudo systemctl start nginx
    sudo systemctl enable nginx
fi

# 2. Configurar banco de dados
log "🗄️ Configurando banco de dados..."

# Criar usuário e banco PostgreSQL
sudo -u postgres psql << EOF
CREATE USER imoveis_user WITH PASSWORD 'secure_password_123';
CREATE DATABASE imoveis_caixa_pro OWNER imoveis_user;
GRANT ALL PRIVILEGES ON DATABASE imoveis_caixa_pro TO imoveis_user;
ALTER USER imoveis_user CREATEDB;
\q
EOF

# 3. Instalar dependências do projeto
log "📦 Instalando dependências do projeto..."

# Composer
if [ -f "composer.json" ]; then
    composer install --optimize-autoloader --no-dev
else
    error "composer.json não encontrado. Execute este script na raiz do projeto."
fi

# NPM
if [ -f "package.json" ]; then
    npm ci
    npm run build
else
    warn "package.json não encontrado. Pulando instalação do Node.js..."
fi

# 4. Configurar arquivo .env
log "⚙️ Configurando arquivo .env..."

if [ ! -f ".env" ]; then
    if [ -f ".env.example" ]; then
        cp .env.example .env
        log "Arquivo .env criado a partir do .env.example"
    else
        error "Arquivo .env.example não encontrado"
    fi
fi

# Gerar chave da aplicação
php artisan key:generate --force

# Configurar banco de dados no .env
sed -i "s/DB_DATABASE=.*/DB_DATABASE=imoveis_caixa_pro/" .env
sed -i "s/DB_USERNAME=.*/DB_USERNAME=imoveis_user/" .env
sed -i "s/DB_PASSWORD=.*/DB_PASSWORD=secure_password_123/" .env

# 5. Configurar permissões
log "🔐 Configurando permissões..."

# Criar diretórios necessários
mkdir -p storage/logs
mkdir -p storage/app/public
mkdir -p storage/framework/{cache,sessions,views}
mkdir -p bootstrap/cache

# Configurar permissões
chmod -R 775 storage
chmod -R 775 bootstrap/cache
chown -R $USER:www-data storage
chown -R $USER:www-data bootstrap/cache

# 6. Executar migrações e seeders
log "🌱 Executando migrações e seeders..."

php artisan migrate --force
php artisan db:seed --force

# Criar link simbólico para storage
php artisan storage:link

# 7. Configurar cache
log "⚡ Configurando cache..."

php artisan config:cache
php artisan route:cache
php artisan view:cache

# 8. Configurar supervisor para queues
log "👷 Configurando supervisor para filas..."

sudo tee /etc/supervisor/conf.d/imoveis-caixa-pro.conf > /dev/null << EOF
[program:imoveis-caixa-pro-worker]
process_name=%(program_name)s_%(process_num)02d
command=php $(pwd)/artisan queue:work redis --sleep=3 --tries=3 --max-time=3600
autostart=true
autorestart=true
stopasgroup=true
killasgroup=true
user=$USER
numprocs=2
redirect_stderr=true
stdout_logfile=$(pwd)/storage/logs/worker.log
stopwaitsecs=3600
EOF

sudo supervisorctl reread
sudo supervisorctl update
sudo supervisorctl start imoveis-caixa-pro-worker:*

# 9. Configurar Nginx
log "🌐 Configurando Nginx..."

sudo tee /etc/nginx/sites-available/imoveis-caixa-pro << EOF
server {
    listen 80;
    listen [::]:80;
    server_name _;
    root $(pwd)/public;

    add_header X-Frame-Options "SAMEORIGIN";
    add_header X-Content-Type-Options "nosniff";

    index index.php;

    charset utf-8;

    location / {
        try_files \$uri \$uri/ /index.php?\$query_string;
    }

    location = /favicon.ico { access_log off; log_not_found off; }
    location = /robots.txt  { access_log off; log_not_found off; }

    error_page 404 /index.php;

    location ~ \.php$ {
        fastcgi_pass unix:/var/run/php/php8.3-fpm.sock;
        fastcgi_param SCRIPT_FILENAME \$realpath_root\$fastcgi_script_name;
        include fastcgi_params;
    }

    location ~ /\.(?!well-known).* {
        deny all;
    }

    # Gzip compression
    gzip on;
    gzip_vary on;
    gzip_min_length 1024;
    gzip_types text/plain text/css text/xml text/javascript application/javascript application/xml+rss application/json;

    # Cache static files
    location ~* \.(jpg|jpeg|png|gif|ico|css|js|woff|woff2)$ {
        expires 1y;
        add_header Cache-Control "public, immutable";
    }
}
EOF

# Ativar site
sudo ln -sf /etc/nginx/sites-available/imoveis-caixa-pro /etc/nginx/sites-enabled/
sudo nginx -t && sudo systemctl reload nginx

# 10. Configurar SSL (Let's Encrypt)
log "🔒 Configurando SSL..."

if command -v certbot &> /dev/null; then
    warn "Certbot já instalado. Para configurar SSL em produção, execute:"
    warn "sudo certbot --nginx -d seu-dominio.com"
else
    sudo apt install -y certbot python3-certbot-nginx
    warn "SSL configurado. Para ativar em produção, execute:"
    warn "sudo certbot --nginx -d seu-dominio.com"
fi

# 11. Configurar cron jobs
log "⏰ Configurando cron jobs..."

# Adicionar cron jobs do Laravel
(crontab -l 2>/dev/null; echo "* * * * * cd $(pwd) && php artisan schedule:run >> /dev/null 2>&1") | crontab -

# 12. Configurar monitoramento
log "📊 Configurando monitoramento..."

# Instalar htop para monitoramento
sudo apt install -y htop iotop

# Configurar logrotate
sudo tee /etc/logrotate.d/imoveis-caixa-pro << EOF
$(pwd)/storage/logs/*.log {
    daily
    missingok
    rotate 14
    compress
    notifempty
    create 0644 $USER $USER
}
EOF

# 13. Configurar backup automático
log "💾 Configurando backup automático..."

mkdir -p ~/backups

# Script de backup
tee ~/backups/backup-imoveis.sh << EOF
#!/bin/bash
BACKUP_DIR=~/backups
DATE=\$(date +%Y%m%d_%H%M%S)
APP_DIR=$(pwd)

# Backup do banco
pg_dump -h localhost -U imoveis_user imoveis_caixa_pro > \$BACKUP_DIR/db_\$DATE.sql

# Backup dos arquivos
tar -czf \$BACKUP_DIR/files_\$DATE.tar.gz -C \$APP_DIR storage/app/public

# Manter apenas 7 backups
find \$BACKUP_DIR -name "*.sql" -mtime +7 -delete
find \$BACKUP_DIR -name "*.tar.gz" -mtime +7 -delete

echo "Backup concluído: \$DATE"
EOF

chmod +x ~/backups/backup-imoveis.sh

# Adicionar ao cron (backup diário às 2h)
(crontab -l 2>/dev/null; echo "0 2 * * * ~/backups/backup-imoveis.sh >> ~/backups/backup.log 2>&1") | crontab -

# 14. Configurar OpenAI Codex
log "🧠 Configurando integração OpenAI Codex..."

# Verificar se a chave da OpenAI está configurada
if [ -z "$OPENAI_API_KEY" ] && ! grep -q "OPENAI_API_KEY=" .env; then
    warn "OPENAI_API_KEY não configurada. Adicione ao .env para usar análise automática de código."
fi

# 15. Testes finais
log "🧪 Executando testes finais..."

# Testar conexão com banco
php artisan tinker --execute="DB::connection()->getPdo(); echo 'Conexão com banco: OK';"

# Testar Redis
php artisan tinker --execute="Redis::ping(); echo 'Conexão com Redis: OK';"

# Testar aplicação
if curl -f -s http://localhost > /dev/null; then
    log "✅ Aplicação respondendo corretamente"
else
    warn "⚠️ Aplicação não está respondendo. Verifique os logs."
fi

# 16. Informações finais
log "🎉 Configuração concluída!"

echo ""
